mapper.insert:
INSERT INTO BANK_ACC_DEPOSIT_ADD
(
    MEMBER_ID
    ,ORG_CD
    ,ACCOUNT_NUM
    ,SEQNO
    ,CURRENCY_CODE
    ,BALANCE_AMT
    ,WITHDRAWABLE_AMT
    ,OFFERED_RATE
    ,LAST_PAID_IN_CNT
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{accountNum, jdbcType=VARCHAR}
    ,#{seqno, jdbcType=VARCHAR}
    ,#{currencyCode, jdbcType=VARCHAR}
    ,#{balanceAmt, jdbcType=NUMERIC}
    ,#{withdrawableAmt, jdbcType=NUMERIC}
    ,#{offeredRate, jdbcType=NUMERIC}
    ,#{lastPaidInCnt, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE BANK_ACC_DEPOSIT_ADD
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,ACCOUNT_NUM = #{accountNum, jdbcType=VARCHAR}
       ,SEQNO = #{seqno, jdbcType=VARCHAR}
       ,CURRENCY_CODE = #{currencyCode, jdbcType=VARCHAR}
       ,BALANCE_AMT = #{balanceAmt, jdbcType=NUMERIC}
       ,WITHDRAWABLE_AMT = #{withdrawableAmt, jdbcType=NUMERIC}
       ,OFFERED_RATE = #{offeredRate, jdbcType=NUMERIC}
       ,LAST_PAID_IN_CNT = #{lastPaidInCnt, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}
   AND SEQNO = #{seqno}

mapper.delete:
DELETE FROM BANK_ACC_DEPOSIT_ADD
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}
   AND SEQNO = #{seqno}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM BANK_ACC_DEPOSIT_ADD
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND ACCOUNT_NUM = #{accountNum}


    AND SEQNO = #{seqno}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM, SEQNO) ROW_NUM ,MEMBER_ID ,ORG_CD ,ACCOUNT_NUM ,SEQNO ,CURRENCY_CODE ,BALANCE_AMT ,WITHDRAWABLE_AMT ,OFFERED_RATE ,LAST_PAID_IN_CNT ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM BANK_ACC_DEPOSIT_ADD WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND ACCOUNT_NUM = #{accountNum}
AND SEQNO = #{seqno}
ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM, SEQNO ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
