mapper.insert:
INSERT INTO BANK_ACC_PROD
(
    MEMBER_ID
    ,ORG_CD
    ,ACCOUNT_NUM
    ,CURRENCY_CODE
    ,BALANCE_AMT
    ,EVAL_AMT
    ,ISSUE_DATE
    ,EXP_DATE
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{accountNum, jdbcType=VARCHAR}
    ,#{currencyCode, jdbcType=VARCHAR}
    ,#{balanceAmt, jdbcType=NUMERIC}
    ,#{evalAmt, jdbcType=NUMERIC}
    ,#{issueDate, jdbcType=VARCHAR}
    ,#{expDate, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE BANK_ACC_PROD
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,ACCOUNT_NUM = #{accountNum, jdbcType=VARCHAR}
       ,CURRENCY_CODE = #{currencyCode, jdbcType=VARCHAR}
       ,BALANCE_AMT = #{balanceAmt, jdbcType=NUMERIC}
       ,EVAL_AMT = #{evalAmt, jdbcType=NUMERIC}
       ,ISSUE_DATE = #{issueDate, jdbcType=VARCHAR}
       ,EXP_DATE = #{expDate, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}

mapper.delete:
DELETE FROM BANK_ACC_PROD
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM BANK_ACC_PROD
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND ACCOUNT_NUM = #{accountNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,ACCOUNT_NUM ,CURRENCY_CODE ,BALANCE_AMT ,EVAL_AMT ,ISSUE_DATE ,EXP_DATE ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM BANK_ACC_PROD WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND ACCOUNT_NUM = #{accountNum}
ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
