mapper.insert:
INSERT INTO BANK_CUST
(
    MEMBER_ID
    ,ORG_CD
    ,REG_DATE
    ,API_TRAN_DAY
    ,API_TRAN_ID
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{regDate, jdbcType=VARCHAR}
    ,#{apiTranDay, jdbcType=VARCHAR}
    ,#{apiTranId, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE BANK_CUST
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,REG_DATE = #{regDate, jdbcType=VARCHAR}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND REG_DATE = #{regDate}

mapper.delete:
DELETE FROM BANK_CUST
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}

        AND REG_DATE = #{regDate}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM BANK_CUST
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND REG_DATE = #{regDate}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, REG_DATE) ROW_NUM ,MEMBER_ID ,ORG_CD ,REG_DATE ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM BANK_CUST WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND REG_DATE = #{regDate}
ORDER BY MEMBER_ID, ORG_CD, REG_DATE ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
