mapper.insert:
INSERT INTO BOND_BAL
(
    MEMBER_ID
    ,ORG_CD
    ,BOND_NUM
    ,PREV_ORG_CODE
    ,IS_DEBT_MGMT
    ,REPAY_METHOD
    ,BALANCE_AMT
    ,BOND_PRINCIPAL
    ,API_TRAN_DAY
    ,API_TRAN_ID
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{bondNum, jdbcType=VARCHAR}
    ,#{prevOrgCode, jdbcType=VARCHAR}
    ,#{isDebtMgmt, jdbcType=VARCHAR}
    ,#{repayMethod, jdbcType=VARCHAR}
    ,#{balanceAmt, jdbcType=NUMERIC}
    ,#{bondPrincipal, jdbcType=NUMERIC}
    ,#{apiTranDay, jdbcType=VARCHAR}
    ,#{apiTranId, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE BOND_BAL
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,BOND_NUM = #{bondNum, jdbcType=VARCHAR}
       ,PREV_ORG_CODE = #{prevOrgCode, jdbcType=VARCHAR}
       ,IS_DEBT_MGMT = #{isDebtMgmt, jdbcType=VARCHAR}
       ,REPAY_METHOD = #{repayMethod, jdbcType=VARCHAR}
       ,BALANCE_AMT = #{balanceAmt, jdbcType=NUMERIC}
       ,BOND_PRINCIPAL = #{bondPrincipal, jdbcType=NUMERIC}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND BOND_NUM = #{bondNum}

mapper.delete:
DELETE FROM BOND_BAL
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND BOND_NUM = #{bondNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM BOND_BAL
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND BOND_NUM = #{bondNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, BOND_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,BOND_NUM ,PREV_ORG_CODE ,IS_DEBT_MGMT ,REPAY_METHOD ,BALANCE_AMT ,BOND_PRINCIPAL ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM BOND_BAL WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND BOND_NUM = #{bondNum}
ORDER BY MEMBER_ID, ORG_CD, BOND_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
