mapper.insert:
INSERT INTO BOND_HIST
(
    MEMBER_ID
    ,ORG_CD
    ,BOND_NUM
    ,TRANS_DTIME
    ,TRANS_NO
    ,TRANS_AMT
    ,BALANCE_AMT
    ,INT_AMT
    ,PRINCIPAL_AMT
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{bondNum, jdbcType=VARCHAR}
    ,#{transDtime, jdbcType=VARCHAR}
    ,#{transNo, jdbcType=VARCHAR}
    ,#{transAmt, jdbcType=VARCHAR}
    ,#{balanceAmt, jdbcType=NUMERIC}
    ,#{intAmt, jdbcType=NUMERIC}
    ,#{principalAmt, jdbcType=NUMERIC}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE BOND_HIST
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,BOND_NUM = #{bondNum, jdbcType=VARCHAR}
       ,TRANS_DTIME = #{transDtime, jdbcType=VARCHAR}
       ,TRANS_NO = #{transNo, jdbcType=VARCHAR}
       ,TRANS_AMT = #{transAmt, jdbcType=VARCHAR}
       ,BALANCE_AMT = #{balanceAmt, jdbcType=NUMERIC}
       ,INT_AMT = #{intAmt, jdbcType=NUMERIC}
       ,PRINCIPAL_AMT = #{principalAmt, jdbcType=NUMERIC}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND BOND_NUM = #{bondNum}
   AND TRANS_DTIME = #{transDtime}

mapper.delete:
DELETE FROM BOND_HIST
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND BOND_NUM = #{bondNum}
   AND TRANS_DTIME = #{transDtime}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM BOND_HIST
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND BOND_NUM = #{bondNum}


    AND TRANS_DTIME = #{transDtime}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, BOND_NUM, TRANS_DTIME) ROW_NUM ,MEMBER_ID ,ORG_CD ,BOND_NUM ,TRANS_DTIME ,TRANS_NO ,TRANS_AMT ,BALANCE_AMT ,INT_AMT ,PRINCIPAL_AMT ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM BOND_HIST WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND BOND_NUM = #{bondNum}
AND TRANS_DTIME = #{transDtime}
ORDER BY MEMBER_ID, ORG_CD, BOND_NUM, TRANS_DTIME ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
