    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE CARD_CHARGE_ADD
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,SEQNO = #{seqno, jdbcType=VARCHAR}
       ,CHARGE_MONTH = #{chargeMonth, jdbcType=VARCHAR}
       ,CARD_ID = #{cardId, jdbcType=VARCHAR}
       ,PAID_DTIME = #{paidDtime, jdbcType=VARCHAR}
       ,TRANS_NO = #{transNo, jdbcType=VARCHAR}
       ,PAID_AMT = #{paidAmt, jdbcType=NUMERIC}
       ,CURRENCY_CODE = #{currencyCode, jdbcType=VARCHAR}
       ,MERCHANT_NAME = #{merchantName, jdbcType=VARCHAR}
       ,MERCHANT_REGNO = #{merchantRegno, jdbcType=VARCHAR}
       ,CREDIT_FEE_AMT = #{creditFeeAmt, jdbcType=NUMERIC}
       ,TOTAL_INSTALL_CNT = #{totalInstallCnt, jdbcType=NUMERIC}
       ,CUR_INSTALL_CNT = #{curInstallCnt, jdbcType=NUMERIC}
       ,BALANCE_AMT = #{balanceAmt, jdbcType=NUMERIC}
       ,PROD_TYPE = #{prodType, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND SEQNO = #{seqno}
   AND CHARGE_MONTH = #{chargeMonth}
   AND CARD_ID = #{cardId}
   AND PAID_DTIME = #{paidDtime}
   AND TRANS_NO = #{transNo}

mapper.delete:
DELETE FROM CARD_CHARGE_ADD
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND SEQNO = #{seqno}
   AND CHARGE_MONTH = #{chargeMonth}
   AND CARD_ID = #{cardId}
   AND PAID_DTIME = #{paidDtime}
   AND TRANS_NO = #{transNo}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM CARD_CHARGE_ADD
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND SEQNO = #{seqno}


    AND CHARGE_MONTH = #{chargeMonth}


    AND CARD_ID = #{cardId}


    AND PAID_DTIME = #{paidDtime}


    AND TRANS_NO = #{transNo}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, SEQNO, CHARGE_MONTH, CARD_ID, PAID_DTIME, TRANS_NO) ROW_NUM ,MEMBER_ID ,ORG_CD ,SEQNO ,CHARGE_MONTH ,CARD_ID ,PAID_DTIME ,TRANS_NO ,PAID_AMT ,CURRENCY_CODE ,MERCHANT_NAME ,MERCHANT_REGNO ,CREDIT_FEE_AMT ,TOTAL_INSTALL_CNT ,CUR_INSTALL_CNT ,BALANCE_AMT ,PROD_TYPE ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM CARD_CHARGE_ADD WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND SEQNO = #{seqno}
AND CHARGE_MONTH = #{chargeMonth}
AND CARD_ID = #{cardId}
AND PAID_DTIME = #{paidDtime}
AND TRANS_NO = #{transNo}
ORDER BY MEMBER_ID, ORG_CD, SEQNO, CHARGE_MONTH, CARD_ID, PAID_DTIME, TRANS_NO ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
