mapper.insert:
INSERT INTO CARD_CHARGE
(
    MEMBER_ID
    ,ORG_CD
    ,SEQNO
    ,CHARGE_AMT
    ,CHARGE_DAY
    ,CHARGE_MONTH
    ,PAID_OUT_DATE
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{seqno, jdbcType=VARCHAR}
    ,#{chargeAmt, jdbcType=NUMERIC}
    ,#{chargeDay, jdbcType=VARCHAR}
    ,#{chargeMonth, jdbcType=VARCHAR}
    ,#{paidOutDate, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE CARD_CHARGE
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,SEQNO = #{seqno, jdbcType=VARCHAR}
       ,CHARGE_AMT = #{chargeAmt, jdbcType=NUMERIC}
       ,CHARGE_DAY = #{chargeDay, jdbcType=VARCHAR}
       ,CHARGE_MONTH = #{chargeMonth, jdbcType=VARCHAR}
       ,PAID_OUT_DATE = #{paidOutDate, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND SEQNO = #{seqno}

mapper.delete:
DELETE FROM CARD_CHARGE
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND SEQNO = #{seqno}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM CARD_CHARGE
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND SEQNO = #{seqno}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, SEQNO) ROW_NUM ,MEMBER_ID ,ORG_CD ,SEQNO ,CHARGE_AMT ,CHARGE_DAY ,CHARGE_MONTH ,PAID_OUT_DATE ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM CARD_CHARGE WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND SEQNO = #{seqno}
ORDER BY MEMBER_ID, ORG_CD, SEQNO ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
