    ,#{approvedAmt, jdbcType=NUMERIC}
    ,#{countryCode, jdbcType=VARCHAR}
    ,#{currencyCode, jdbcType=VARCHAR}
    ,#{krwAmt, jdbcType=NUMERIC}
    ,#{apiTranDay, jdbcType=VARCHAR}
