    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE CARD_OS_PCA
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,CARD_ID = #{cardId, jdbcType=VARCHAR}
       ,PURCHASE_NUM = #{purchaseNum, jdbcType=VARCHAR}
       ,PURCHASE_DATE = #{purchaseDate, jdbcType=VARCHAR}
       ,SALES_DATE = #{salesDate, jdbcType=VARCHAR}
       ,PRI_APPROVED_NUM = #{priApprovedNum, jdbcType=VARCHAR}
       ,PRI_APPROVED_DTIME = #{priApprovedDtime, jdbcType=VARCHAR}
       ,PRI_APPROVED_AMT = #{priApprovedAmt, jdbcType=NUMERIC}
       ,PRI_KRW_AMT = #{priKrwAmt, jdbcType=NUMERIC}
       ,PRI_CURRENCY_CODE = #{priCurrencyCode, jdbcType=VARCHAR}
       ,PAY_TYPE = #{payType, jdbcType=VARCHAR}
       ,MERCHANT_NAME = #{merchantName, jdbcType=VARCHAR}
       ,APPROVED_AMT = #{approvedAmt, jdbcType=NUMERIC}
       ,COUNTRY_CODE = #{countryCode, jdbcType=VARCHAR}
       ,CURRENCY_CODE = #{currencyCode, jdbcType=VARCHAR}
       ,KRW_AMT = #{krwAmt, jdbcType=NUMERIC}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND CARD_ID = #{cardId}
   AND PURCHASE_NUM = #{purchaseNum}

mapper.delete:
DELETE FROM CARD_OS_PCA
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND CARD_ID = #{cardId}
   AND PURCHASE_NUM = #{purchaseNum}

mapper.select:
SELECT C1.MEMBER_ID ,C1.ORG_CD ,C1.CARD_ID ,C1.PURCHASE_NUM ,C1.PURCHASE_DATE ,C1.SALES_DATE ,C1.PRI_APPROVED_NUM ,C1.PRI_APPROVED_DTIME ,C1.PRI_APPROVED_AMT ,C1.PRI_KRW_AMT ,C1.PRI_CURRENCY_CODE ,C1.PAY_TYPE ,C1.MERCHANT_NAME ,C1.APPROVED_AMT ,C1.COUNTRY_CODE ,C1.CURRENCY_CODE ,C1.KRW_AMT ,C1.API_TRAN_DAY ,C1.API_TRAN_ID ,C1.REG_USER_ID ,C1.REG_DT ,C1.CHG_USER_ID ,C1.CHG_DT FROM CARD_OS_PCA C1, ( SELECT * FROM ( SELECT MEMBER_ID, ORG_CD, CARD_ID, PURCHASE_NUM FROM CARD_OS_PCA WHERE MEMBER_ID = #{memberId} AND ORG_CD = #{orgCd}
AND CARD_ID = #{cardId}
AND PURCHASE_NUM = #{purchaseNum}
ORDER BY MEMBER_ID, ORG_CD, CARD_ID, PURCHASE_NUM DESC ) WHERE rownum = 1 ) C2 WHERE C1.MEMBER_ID = #{memberId} AND C1.ORG_CD = #{orgCd} AND C1.CARD_ID = #{cardId} AND C1.PURCHASE_NUM = #{purchaseNum}

  SELECT 
      COUNT(*) 
 FROM CARD_OS_PCA
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND CARD_ID = #{cardId}


    AND PURCHASE_NUM = #{purchaseNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, CARD_ID, PURCHASE_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,CARD_ID ,PURCHASE_NUM ,PURCHASE_DATE ,SALES_DATE ,PRI_APPROVED_NUM ,PRI_APPROVED_DTIME ,PRI_APPROVED_AMT ,PRI_KRW_AMT ,PRI_CURRENCY_CODE ,PAY_TYPE ,MERCHANT_NAME ,APPROVED_AMT ,COUNTRY_CODE ,CURRENCY_CODE ,KRW_AMT ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM CARD_OS_PCA WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND CARD_ID = #{cardId}
AND PURCHASE_NUM = #{purchaseNum}
ORDER BY MEMBER_ID, ORG_CD, CARD_ID, PURCHASE_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
