    ,KRW_AMT
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{cardId, jdbcType=VARCHAR}
    ,#{purchaseNum, jdbcType=VARCHAR}
    ,#{purchaseDate, jdbcType=VARCHAR}
    ,#{salesDate, jdbcType=VARCHAR}
    ,#{priApprovedNum, jdbcType=VARCHAR}
    ,#{priApprovedDtime, jdbcType=VARCHAR}
    ,#{priApprovedAmt, jdbcType=NUMERIC}
    ,#{priKrwAmt, jdbcType=NUMERIC}
    ,#{priCurrencyCode, jdbcType=VARCHAR}
    ,#{payType, jdbcType=VARCHAR}
    ,#{merchantName, jdbcType=VARCHAR}
    ,#{approvedAmt, jdbcType=NUMERIC}
    ,#{countryCode, jdbcType=VARCHAR}
    ,#{currencyCode, jdbcType=VARCHAR}
    ,#{krwAmt, jdbcType=NUMERIC}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE CARD_OS_PCA
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,CARD_ID = #{cardId, jdbcType=VARCHAR}
       ,PURCHASE_NUM = #{purchaseNum, jdbcType=VARCHAR}
       ,PURCHASE_DATE = #{purchaseDate, jdbcType=VARCHAR}
       ,SALES_DATE = #{salesDate, jdbcType=VARCHAR}
       ,PRI_APPROVED_NUM = #{priApprovedNum, jdbcType=VARCHAR}
       ,PRI_APPROVED_DTIME = #{priApprovedDtime, jdbcType=VARCHAR}
       ,PRI_APPROVED_AMT = #{priApprovedAmt, jdbcType=NUMERIC}
       ,PRI_KRW_AMT = #{priKrwAmt, jdbcType=NUMERIC}
       ,PRI_CURRENCY_CODE = #{priCurrencyCode, jdbcType=VARCHAR}
       ,PAY_TYPE = #{payType, jdbcType=VARCHAR}
       ,MERCHANT_NAME = #{merchantName, jdbcType=VARCHAR}
       ,APPROVED_AMT = #{approvedAmt, jdbcType=NUMERIC}
       ,COUNTRY_CODE = #{countryCode, jdbcType=VARCHAR}
       ,CURRENCY_CODE = #{currencyCode, jdbcType=VARCHAR}
       ,KRW_AMT = #{krwAmt, jdbcType=NUMERIC}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND CARD_ID = #{cardId}
   AND PURCHASE_NUM = #{purchaseNum}

mapper.delete:
DELETE FROM CARD_OS_PCA
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND CARD_ID = #{cardId}
   AND PURCHASE_NUM = #{purchaseNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM CARD_OS_PCA
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND CARD_ID = #{cardId}


    AND PURCHASE_NUM = #{purchaseNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, CARD_ID, PURCHASE_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,CARD_ID ,PURCHASE_NUM ,PURCHASE_DATE ,SALES_DATE ,PRI_APPROVED_NUM ,PRI_APPROVED_DTIME ,PRI_APPROVED_AMT ,PRI_KRW_AMT ,PRI_CURRENCY_CODE ,PAY_TYPE ,MERCHANT_NAME ,APPROVED_AMT ,COUNTRY_CODE ,CURRENCY_CODE ,KRW_AMT ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM CARD_OS_PCA WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND CARD_ID = #{cardId}
AND PURCHASE_NUM = #{purchaseNum}
ORDER BY MEMBER_ID, ORG_CD, CARD_ID, PURCHASE_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
