mapper.insert:
INSERT INTO DB
(
    MEMBER_ID
    ,ORG_CD
    ,IS_RUNNING_PENSION
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{isRunningPension, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE DB
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,IS_RUNNING_PENSION = #{isRunningPension, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND IS_RUNNING_PENSION = #{isRunningPension}

mapper.delete:
DELETE FROM DB
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND IS_RUNNING_PENSION = #{isRunningPension}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM DB
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND IS_RUNNING_PENSION = #{isRunningPension}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, IS_RUNNING_PENSION) ROW_NUM ,MEMBER_ID ,ORG_CD ,IS_RUNNING_PENSION ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM DB WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND IS_RUNNING_PENSION = #{isRunningPension}
ORDER BY MEMBER_ID, ORG_CD, IS_RUNNING_PENSION ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
