mapper.insert:
INSERT INTO DC_BASE
(
    MEMBER_ID
    ,ORG_CD
    ,DC_NUM
    ,BALANCE_AMT
    ,EVAL_AMT
    ,ISSUE_DATE
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{dcNum, jdbcType=VARCHAR}
    ,#{balanceAmt, jdbcType=NUMERIC}
    ,#{evalAmt, jdbcType=NUMERIC}
    ,#{issueDate, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE DC_BASE
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,DC_NUM = #{dcNum, jdbcType=VARCHAR}
       ,BALANCE_AMT = #{balanceAmt, jdbcType=NUMERIC}
       ,EVAL_AMT = #{evalAmt, jdbcType=NUMERIC}
       ,ISSUE_DATE = #{issueDate, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND DC_NUM = #{dcNum}

mapper.delete:
DELETE FROM DC_BASE
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND DC_NUM = #{dcNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM DC_BASE
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND DC_NUM = #{dcNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, DC_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,DC_NUM ,BALANCE_AMT ,EVAL_AMT ,ISSUE_DATE ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM DC_BASE WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND DC_NUM = #{dcNum}
ORDER BY MEMBER_ID, ORG_CD, DC_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
