mapper.insert:
INSERT INTO DC
(
    MEMBER_ID
    ,ORG_CD
    ,DC_NUM
    ,DC_NAME
    ,IS_CONSENT
    ,API_TRAN_DAY
    ,API_TRAN_ID
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{dcNum, jdbcType=VARCHAR}
    ,#{dcName, jdbcType=VARCHAR}
    ,#{isConsent, jdbcType=VARCHAR}
    ,#{apiTranDay, jdbcType=VARCHAR}
    ,#{apiTranId, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE DC
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,DC_NUM = #{dcNum, jdbcType=VARCHAR}
       ,DC_NAME = #{dcName, jdbcType=VARCHAR}
       ,IS_CONSENT = #{isConsent, jdbcType=VARCHAR}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND DC_NUM = #{dcNum}

mapper.delete:
DELETE FROM DC
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND DC_NUM = #{dcNum}

DELETE FROM DC
WHERE
    MEMBER_ID = #{memberId}
  AND ORG_CD = #{orgCd}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM DC
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND DC_NUM = #{dcNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, DC_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,DC_NUM ,DC_NAME ,IS_CONSENT ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM DC WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND DC_NUM = #{dcNum}
ORDER BY MEMBER_ID, ORG_CD, DC_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
