mapper.insert:
INSERT INTO ELEC_PAY_METHOD
(
    MEMBER_ID
    ,ORG_CD
    ,SUB_KEY
    ,PAY_ORG_CODE
    ,PAY_TYPE
    ,PAY_ID
    ,IS_PRIMARY
    ,API_TRAN_DAY
    ,API_TRAN_ID
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{subKey, jdbcType=VARCHAR}
    ,#{payOrgCode, jdbcType=VARCHAR}
    ,#{payType, jdbcType=VARCHAR}
    ,#{payId, jdbcType=VARCHAR}
    ,#{isPrimary, jdbcType=VARCHAR}
    ,#{apiTranDay, jdbcType=VARCHAR}
    ,#{apiTranId, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE ELEC_PAY_METHOD
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,SUB_KEY = #{subKey, jdbcType=VARCHAR}
       ,PAY_ORG_CODE = #{payOrgCode, jdbcType=VARCHAR}
       ,PAY_TYPE = #{payType, jdbcType=VARCHAR}
       ,PAY_ID = #{payId, jdbcType=VARCHAR}
       ,IS_PRIMARY = #{isPrimary, jdbcType=VARCHAR}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND SUB_KEY = #{subKey}
   AND PAY_ORG_CODE = #{payOrgCode}

mapper.delete:
DELETE FROM ELEC_PAY_METHOD
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND SUB_KEY = #{subKey}
   AND PAY_ORG_CODE = #{payOrgCode}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM ELEC_PAY_METHOD
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND SUB_KEY = #{subKey}


    AND PAY_ORG_CODE = #{payOrgCode}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, SUB_KEY, PAY_ORG_CODE) ROW_NUM ,MEMBER_ID ,ORG_CD ,SUB_KEY ,PAY_ORG_CODE ,PAY_TYPE ,PAY_ID ,IS_PRIMARY ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM ELEC_PAY_METHOD WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND SUB_KEY = #{subKey}
AND PAY_ORG_CODE = #{payOrgCode}
ORDER BY MEMBER_ID, ORG_CD, SUB_KEY, PAY_ORG_CODE ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
