mapper.insert:
INSERT INTO ELEC_PPAY_ACC
(
    MEMBER_ID
    ,ORG_CD
    ,FOB_ID
    ,ACCOUNT_ID
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{fobId, jdbcType=VARCHAR}
    ,#{accountId, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE ELEC_PPAY_ACC
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,FOB_ID = #{fobId, jdbcType=VARCHAR}
       ,ACCOUNT_ID = #{accountId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND FOB_ID = #{fobId}
   AND ACCOUNT_ID = #{accountId}

mapper.delete:
DELETE FROM ELEC_PPAY_ACC
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND FOB_ID = #{fobId}
   AND ACCOUNT_ID = #{accountId}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM ELEC_PPAY_ACC
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND FOB_ID = #{fobId}


    AND ACCOUNT_ID = #{accountId}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, FOB_ID, ACCOUNT_ID) ROW_NUM ,MEMBER_ID ,ORG_CD ,FOB_ID ,ACCOUNT_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM ELEC_PPAY_ACC WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND FOB_ID = #{fobId}
AND ACCOUNT_ID = #{accountId}
ORDER BY MEMBER_ID, ORG_CD, FOB_ID, ACCOUNT_ID ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
