mapper.update:
UPDATE ELEC_PPAY_AUTO_CHARGE
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,FOB_ID = #{fobId, jdbcType=VARCHAR}
       ,ACCOUNT_ID = #{accountId, jdbcType=VARCHAR}
       ,AUTO_CHARGE_SEQ = #{autoChargeSeq, jdbcType=NUMERIC}
       ,CHARGE_ORG_CODE = #{chargeOrgCode, jdbcType=VARCHAR}
       ,CHARGE_ACCOUNT_NUM = #{chargeAccountNum, jdbcType=VARCHAR}
       ,CHARGE_OPTION = #{chargeOption, jdbcType=VARCHAR}
       ,CHARGE_DAY = #{chargeDay, jdbcType=VARCHAR}
       ,CHARGE_BASE_AMT = #{chargeBaseAmt, jdbcType=NUMERIC}
       ,CHARGE_AMT = #{chargeAmt, jdbcType=NUMERIC}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND FOB_ID = #{fobId}
   AND ACCOUNT_ID = #{accountId}
   AND AUTO_CHARGE_SEQ = #{autoChargeSeq}
   AND CHARGE_ORG_CODE = #{chargeOrgCode}

mapper.delete:
DELETE FROM ELEC_PPAY_AUTO_CHARGE
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND FOB_ID = #{fobId}
   AND ACCOUNT_ID = #{accountId}
   AND AUTO_CHARGE_SEQ = #{autoChargeSeq}
   AND CHARGE_ORG_CODE = #{chargeOrgCode}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM ELEC_PPAY_AUTO_CHARGE
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND FOB_ID = #{fobId}


    AND ACCOUNT_ID = #{accountId}


    AND AUTO_CHARGE_SEQ = #{autoChargeSeq}


    AND CHARGE_ORG_CODE = #{chargeOrgCode}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, FOB_ID, ACCOUNT_ID, AUTO_CHARGE_SEQ, CHARGE_ORG_CODE) ROW_NUM ,MEMBER_ID ,ORG_CD ,FOB_ID ,ACCOUNT_ID ,AUTO_CHARGE_SEQ ,CHARGE_ORG_CODE ,CHARGE_ACCOUNT_NUM ,CHARGE_OPTION ,CHARGE_DAY ,CHARGE_BASE_AMT ,CHARGE_AMT ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM ELEC_PPAY_AUTO_CHARGE WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND FOB_ID = #{fobId}
AND ACCOUNT_ID = #{accountId}
AND AUTO_CHARGE_SEQ = #{autoChargeSeq}
AND CHARGE_ORG_CODE = #{chargeOrgCode}
ORDER BY MEMBER_ID, ORG_CD, FOB_ID, ACCOUNT_ID, AUTO_CHARGE_SEQ, CHARGE_ORG_CODE ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
