mapper.update:
UPDATE GURT_BASE
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,INSU_NUM = #{insuNum, jdbcType=VARCHAR}
       ,ISSUE_DATE = #{issueDate, jdbcType=VARCHAR}
       ,EXP_DATE = #{expDate, jdbcType=VARCHAR}
       ,FACE_AMT = #{faceAmt, jdbcType=NUMERIC}
       ,PAY_DUE = #{payDue, jdbcType=VARCHAR}
       ,PAY_AMT = #{payAmt, jdbcType=NUMERIC}
       ,INSURED_CNT = #{insuredCnt, jdbcType=NUMERIC}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}

mapper.delete:
DELETE FROM GURT_BASE
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM GURT_BASE
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND INSU_NUM = #{insuNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, INSU_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,INSU_NUM ,ISSUE_DATE ,EXP_DATE ,FACE_AMT ,PAY_DUE ,PAY_AMT ,INSURED_CNT ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM GURT_BASE WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND INSU_NUM = #{insuNum}
ORDER BY MEMBER_ID, ORG_CD, INSU_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
