mapper.insert:
INSERT INTO GURT_HIST
(
    MEMBER_ID
    ,ORG_CD
    ,INSU_NUM
    ,PAID_IN_CNT
    ,TRANS_DATE
    ,PAID_AMT
    ,PAY_METHOD
    ,API_TRAN_DAY
    ,API_TRAN_ID
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{insuNum, jdbcType=VARCHAR}
    ,#{paidInCnt, jdbcType=NUMERIC}
    ,#{transDate, jdbcType=VARCHAR}
    ,#{paidAmt, jdbcType=NUMERIC}
    ,#{payMethod, jdbcType=VARCHAR}
    ,#{apiTranDay, jdbcType=VARCHAR}
    ,#{apiTranId, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE GURT_HIST
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,INSU_NUM = #{insuNum, jdbcType=VARCHAR}
       ,PAID_IN_CNT = #{paidInCnt, jdbcType=NUMERIC}
       ,TRANS_DATE = #{transDate, jdbcType=VARCHAR}
       ,PAID_AMT = #{paidAmt, jdbcType=NUMERIC}
       ,PAY_METHOD = #{payMethod, jdbcType=VARCHAR}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND PAID_IN_CNT = #{paidInCnt}
   AND TRANS_DATE = #{transDate}

mapper.delete:
DELETE FROM GURT_HIST
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND PAID_IN_CNT = #{paidInCnt}
   AND TRANS_DATE = #{transDate}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM GURT_HIST
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND INSU_NUM = #{insuNum}


    AND PAID_IN_CNT = #{paidInCnt}


    AND TRANS_DATE = #{transDate}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, PAID_IN_CNT, TRANS_DATE) ROW_NUM ,MEMBER_ID ,ORG_CD ,INSU_NUM ,PAID_IN_CNT ,TRANS_DATE ,PAID_AMT ,PAY_METHOD ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM GURT_HIST WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND INSU_NUM = #{insuNum}
AND PAID_IN_CNT = #{paidInCnt}
AND TRANS_DATE = #{transDate}
ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, PAID_IN_CNT, TRANS_DATE ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
