mapper.insert:
INSERT INTO GURT_INSD
(
    MEMBER_ID
    ,ORG_CD
    ,INSU_NUM
    ,INSURED_NAME
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{insuNum, jdbcType=VARCHAR}
    ,#{insuredName, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE GURT_INSD
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,INSU_NUM = #{insuNum, jdbcType=VARCHAR}
       ,INSURED_NAME = #{insuredName, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND INSURED_NAME = #{insuredName}

mapper.delete:
DELETE FROM GURT_INSD
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND INSURED_NAME = #{insuredName}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM GURT_INSD
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND INSU_NUM = #{insuNum}


    AND INSURED_NAME = #{insuredName}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, INSURED_NAME) ROW_NUM ,MEMBER_ID ,ORG_CD ,INSU_NUM ,INSURED_NAME ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM GURT_INSD WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND INSU_NUM = #{insuNum}
AND INSURED_NAME = #{insuredName}
ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, INSURED_NAME ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
