mapper.update:
UPDATE GURT
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,INSU_NUM = #{insuNum, jdbcType=VARCHAR}
       ,IS_CONSENT = #{isConsent, jdbcType=VARCHAR}
       ,PROD_NAME = #{prodName, jdbcType=VARCHAR}
       ,INSU_TYPE = #{insuType, jdbcType=VARCHAR}
       ,INSU_STATUS = #{insuStatus, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}

mapper.delete:
DELETE FROM GURT
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM GURT
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND INSU_NUM = #{insuNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, INSU_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,INSU_NUM ,IS_CONSENT ,PROD_NAME ,INSU_TYPE ,INSU_STATUS ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM GURT WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND INSU_NUM = #{insuNum}
ORDER BY MEMBER_ID, ORG_CD, INSU_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
