       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM INSU_BASE_INSD
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND INSU_NUM = #{insuNum}


    AND INSURED_NO = #{insuredNo}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, INSURED_NO) ROW_NUM ,MEMBER_ID ,ORG_CD ,INSU_NUM ,INSURED_NO ,INSURED_NAME ,IS_PRIMARY ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM INSU_BASE_INSD WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND INSU_NUM = #{insuNum}
AND INSURED_NO = #{insuredNo}
ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, INSURED_NO ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
