mapper.insert:
INSERT INTO INSU_CAR_HIST
(
    MEMBER_ID
    ,ORG_CD
    ,INSU_NUM
    ,CAR_NUMBER
    ,TRANS_DTIME
    ,TRANS_NO
    ,FACE_AMT
    ,PAID_IN_CNT
    ,PAID_AMT
    ,PAY_METHOD
    ,PAY_ORG_CODE
    ,PAY_ACCOUNT_NUM
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{insuNum, jdbcType=VARCHAR}
    ,#{carNumber, jdbcType=VARCHAR}
    ,#{transDtime, jdbcType=VARCHAR}
    ,#{transNo, jdbcType=VARCHAR}
    ,#{faceAmt, jdbcType=NUMERIC}
    ,#{paidInCnt, jdbcType=NUMERIC}
    ,#{paidAmt, jdbcType=NUMERIC}
    ,#{payMethod, jdbcType=VARCHAR}
    ,#{payOrgCode, jdbcType=VARCHAR}
    ,#{payAccountNum, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE INSU_CAR_HIST
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,INSU_NUM = #{insuNum, jdbcType=VARCHAR}
       ,CAR_NUMBER = #{carNumber, jdbcType=VARCHAR}
       ,TRANS_DTIME = #{transDtime, jdbcType=VARCHAR}
       ,TRANS_NO = #{transNo, jdbcType=VARCHAR}
       ,FACE_AMT = #{faceAmt, jdbcType=NUMERIC}
       ,PAID_IN_CNT = #{paidInCnt, jdbcType=NUMERIC}
       ,PAID_AMT = #{paidAmt, jdbcType=NUMERIC}
       ,PAY_METHOD = #{payMethod, jdbcType=VARCHAR}
       ,PAY_ORG_CODE = #{payOrgCode, jdbcType=VARCHAR}
       ,PAY_ACCOUNT_NUM = #{payAccountNum, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND CAR_NUMBER = #{carNumber}
   AND TRANS_DTIME = #{transDtime}
   AND TRANS_NO = #{transNo}

mapper.delete:
DELETE FROM INSU_CAR_HIST
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND CAR_NUMBER = #{carNumber}
   AND TRANS_DTIME = #{transDtime}
   AND TRANS_NO = #{transNo}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM INSU_CAR_HIST
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND INSU_NUM = #{insuNum}


    AND CAR_NUMBER = #{carNumber}


    AND TRANS_DTIME = #{transDtime}


    AND TRANS_NO = #{transNo}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, CAR_NUMBER, TRANS_DTIME, TRANS_NO) ROW_NUM ,MEMBER_ID ,ORG_CD ,INSU_NUM ,CAR_NUMBER ,TRANS_DTIME ,TRANS_NO ,FACE_AMT ,PAID_IN_CNT ,PAID_AMT ,PAY_METHOD ,PAY_ORG_CODE ,PAY_ACCOUNT_NUM ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM INSU_CAR_HIST WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND INSU_NUM = #{insuNum}
AND CAR_NUMBER = #{carNumber}
AND TRANS_DTIME = #{transDtime}
AND TRANS_NO = #{transNo}
ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, CAR_NUMBER, TRANS_DTIME, TRANS_NO ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
