    ,SUBJECT
    ,RELATION
    ,STATUS
    ,COVERAGE_CODE
    ,TYPE
    ,COVERAGE_NUM
    ,COVERAGE_NAME
    ,COVERAGE_AMT
    ,START_DATE
    ,END_DATE
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{insuNum, jdbcType=VARCHAR}
    ,#{subject, jdbcType=VARCHAR}
    ,#{relation, jdbcType=VARCHAR}
    ,#{status, jdbcType=VARCHAR}
    ,#{coverageCode, jdbcType=VARCHAR}
    ,#{type, jdbcType=NUMERIC}
    ,#{coverageNum, jdbcType=VARCHAR}
    ,#{coverageName, jdbcType=VARCHAR}
    ,#{coverageAmt, jdbcType=NUMERIC}
    ,#{startDate, jdbcType=VARCHAR}
    ,#{endDate, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE INSU_GURT
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,INSU_NUM = #{insuNum, jdbcType=VARCHAR}
       ,SUBJECT = #{subject, jdbcType=VARCHAR}
       ,RELATION = #{relation, jdbcType=VARCHAR}
       ,STATUS = #{status, jdbcType=VARCHAR}
       ,COVERAGE_CODE = #{coverageCode, jdbcType=VARCHAR}
       ,TYPE = #{type, jdbcType=NUMERIC}
       ,COVERAGE_NUM = #{coverageNum, jdbcType=VARCHAR}
       ,COVERAGE_NAME = #{coverageName, jdbcType=VARCHAR}
       ,COVERAGE_AMT = #{coverageAmt, jdbcType=NUMERIC}
       ,START_DATE = #{startDate, jdbcType=VARCHAR}
       ,END_DATE = #{endDate, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND SUBJECT = #{subject}
   AND RELATION = #{relation}

mapper.delete:
DELETE FROM INSU_GURT
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND SUBJECT = #{subject}
   AND RELATION = #{relation}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM INSU_GURT
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND INSU_NUM = #{insuNum}


    AND SUBJECT = #{subject}


    AND RELATION = #{relation}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, SUBJECT, RELATION) ROW_NUM ,MEMBER_ID ,ORG_CD ,INSU_NUM ,SUBJECT ,RELATION ,STATUS ,COVERAGE_CODE ,TYPE ,COVERAGE_NUM ,COVERAGE_NAME ,COVERAGE_AMT ,START_DATE ,END_DATE ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM INSU_GURT WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND INSU_NUM = #{insuNum}
AND SUBJECT = #{subject}
AND RELATION = #{relation}
ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, SUBJECT, RELATION ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
