mapper.insert:
INSERT INTO INSU_HIST
(
    MEMBER_ID
    ,ORG_CD
    ,INSU_NUM
    ,TRANS_DATE
    ,TRANS_APPLIED_MONTH
    ,PAID_IN_CNT
    ,PAID_AMT
    ,CURRENCY_CODE
    ,PAY_METHOD
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{insuNum, jdbcType=VARCHAR}
    ,#{transDate, jdbcType=VARCHAR}
    ,#{transAppliedMonth, jdbcType=NUMERIC}
    ,#{paidInCnt, jdbcType=NUMERIC}
    ,#{paidAmt, jdbcType=NUMERIC}
    ,#{currencyCode, jdbcType=VARCHAR}
    ,#{payMethod, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE INSU_HIST
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,INSU_NUM = #{insuNum, jdbcType=VARCHAR}
       ,TRANS_DATE = #{transDate, jdbcType=VARCHAR}
       ,TRANS_APPLIED_MONTH = #{transAppliedMonth, jdbcType=NUMERIC}
       ,PAID_IN_CNT = #{paidInCnt, jdbcType=NUMERIC}
       ,PAID_AMT = #{paidAmt, jdbcType=NUMERIC}
       ,CURRENCY_CODE = #{currencyCode, jdbcType=VARCHAR}
       ,PAY_METHOD = #{payMethod, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND TRANS_DATE = #{transDate}
   AND TRANS_APPLIED_MONTH = #{transAppliedMonth}
   AND PAID_IN_CNT = #{paidInCnt}

mapper.delete:
DELETE FROM INSU_HIST
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND TRANS_DATE = #{transDate}
   AND TRANS_APPLIED_MONTH = #{transAppliedMonth}
   AND PAID_IN_CNT = #{paidInCnt}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM INSU_HIST
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND INSU_NUM = #{insuNum}


    AND TRANS_DATE = #{transDate}


    AND TRANS_APPLIED_MONTH = #{transAppliedMonth}


    AND PAID_IN_CNT = #{paidInCnt}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, TRANS_DATE, TRANS_APPLIED_MONTH, PAID_IN_CNT) ROW_NUM ,MEMBER_ID ,ORG_CD ,INSU_NUM ,TRANS_DATE ,TRANS_APPLIED_MONTH ,PAID_IN_CNT ,PAID_AMT ,CURRENCY_CODE ,PAY_METHOD ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM INSU_HIST WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND INSU_NUM = #{insuNum}
AND TRANS_DATE = #{transDate}
AND TRANS_APPLIED_MONTH = #{transAppliedMonth}
AND PAID_IN_CNT = #{paidInCnt}
ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, TRANS_DATE, TRANS_APPLIED_MONTH, PAID_IN_CNT ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
