mapper.update:
UPDATE INSU_INSD_MI_BASE_PP
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,INSU_NUM = #{insuNum, jdbcType=VARCHAR}
       ,PRIZE_NUM = #{prizeNum, jdbcType=VARCHAR}
       ,IS_PERSON = #{isPerson, jdbcType=VARCHAR}
       ,PRIZE_NAME = #{prizeName, jdbcType=VARCHAR}
       ,IS_PRIMARY = #{isPrimary, jdbcType=VARCHAR}
       ,PRIZE_ADDR = #{prizeAddr, jdbcType=VARCHAR}
       ,OBJECT_CODE = #{objectCode, jdbcType=VARCHAR}
       ,PRIZE_CODE = #{prizeCode, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND PRIZE_NUM = #{prizeNum}

mapper.delete:
DELETE FROM INSU_INSD_MI_BASE_PP
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}
   AND PRIZE_NUM = #{prizeNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM INSU_INSD_MI_BASE_PP
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND INSU_NUM = #{insuNum}


    AND PRIZE_NUM = #{prizeNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, PRIZE_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,INSU_NUM ,PRIZE_NUM ,IS_PERSON ,PRIZE_NAME ,IS_PRIMARY ,PRIZE_ADDR ,OBJECT_CODE ,PRIZE_CODE ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM INSU_INSD_MI_BASE_PP WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND INSU_NUM = #{insuNum}
AND PRIZE_NUM = #{prizeNum}
ORDER BY MEMBER_ID, ORG_CD, INSU_NUM, PRIZE_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
