mapper.update:
UPDATE INSU_PAY
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,INSU_NUM = #{insuNum, jdbcType=VARCHAR}
       ,PAY_DUE = #{payDue, jdbcType=VARCHAR}
       ,PAY_CYCLE = #{payCycle, jdbcType=VARCHAR}
       ,PAY_CNT = #{payCnt, jdbcType=NUMERIC}
       ,PAY_ORG_CODE = #{payOrgCode, jdbcType=VARCHAR}
       ,PAY_ACCOUNT_NUM = #{payAccountNum, jdbcType=VARCHAR}
       ,PAY_DATE = #{payDate, jdbcType=VARCHAR}
       ,PAY_END_DATE = #{payEndDate, jdbcType=VARCHAR}
       ,PAY_AMT = #{payAmt, jdbcType=NUMERIC}
       ,CURRENCY_CODE = #{currencyCode, jdbcType=VARCHAR}
       ,IS_AUTO_PAY = #{isAutoPay, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}

mapper.delete:
DELETE FROM INSU_PAY
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND INSU_NUM = #{insuNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM INSU_PAY
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND INSU_NUM = #{insuNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, INSU_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,INSU_NUM ,PAY_DUE ,PAY_CYCLE ,PAY_CNT ,PAY_ORG_CODE ,PAY_ACCOUNT_NUM ,PAY_DATE ,PAY_END_DATE ,PAY_AMT ,CURRENCY_CODE ,IS_AUTO_PAY ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM INSU_PAY WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND INSU_NUM = #{insuNum}
ORDER BY MEMBER_ID, ORG_CD, INSU_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
