    MEMBER_ID
    ,ORG_CD
    ,ACCOUNT_NUM
    ,CURRENCY_CODE
    ,WITHHOLDINGS_AMT
    ,CREDIT_LOAN_AMT
    ,MORTGAGE_AMT
    ,AVAIL_BALANCE
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{accountNum, jdbcType=VARCHAR}
    ,#{currencyCode, jdbcType=VARCHAR}
    ,#{withholdingsAmt, jdbcType=NUMERIC}
    ,#{creditLoanAmt, jdbcType=NUMERIC}
    ,#{mortgageAmt, jdbcType=NUMERIC}
    ,#{availBalance, jdbcType=NUMERIC}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE INVT_ACC_BASE
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,ACCOUNT_NUM = #{accountNum, jdbcType=VARCHAR}
       ,CURRENCY_CODE = #{currencyCode, jdbcType=VARCHAR}
       ,WITHHOLDINGS_AMT = #{withholdingsAmt, jdbcType=NUMERIC}
       ,CREDIT_LOAN_AMT = #{creditLoanAmt, jdbcType=NUMERIC}
       ,MORTGAGE_AMT = #{mortgageAmt, jdbcType=NUMERIC}
       ,AVAIL_BALANCE = #{availBalance, jdbcType=NUMERIC}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}

mapper.delete:
DELETE FROM INVT_ACC_BASE
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM INVT_ACC_BASE
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND ACCOUNT_NUM = #{accountNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,ACCOUNT_NUM ,CURRENCY_CODE ,WITHHOLDINGS_AMT ,CREDIT_LOAN_AMT ,MORTGAGE_AMT ,AVAIL_BALANCE ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM INVT_ACC_BASE WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND ACCOUNT_NUM = #{accountNum}
ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
