mapper.update:
UPDATE INVT_PENSION_ACC_ADD
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,ACCOUNT_NUM = #{accountNum, jdbcType=VARCHAR}
       ,ISSUE_DATE = #{issueDate, jdbcType=VARCHAR}
       ,PAID_IN_AMT = #{paidInAmt, jdbcType=NUMERIC}
       ,WITHDRAWAL_AMT = #{withdrawalAmt, jdbcType=NUMERIC}
       ,LAST_PAID_IN_DATE = #{lastPaidInDate, jdbcType=VARCHAR}
       ,RCV_AMT = #{rcvAmt, jdbcType=NUMERIC}
       ,PENSION_RCV_START_DATE = #{pensionRcvStartDate, jdbcType=VARCHAR}
       ,ANNUAL_PAY_AMOUNT = #{annualPayAmount, jdbcType=NUMERIC}
       ,CURRENCY_CODE = #{currencyCode, jdbcType=VARCHAR}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}

mapper.delete:
DELETE FROM INVT_PENSION_ACC_ADD
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM INVT_PENSION_ACC_ADD
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND ACCOUNT_NUM = #{accountNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,ACCOUNT_NUM ,ISSUE_DATE ,PAID_IN_AMT ,WITHDRAWAL_AMT ,LAST_PAID_IN_DATE ,RCV_AMT ,PENSION_RCV_START_DATE ,ANNUAL_PAY_AMOUNT ,CURRENCY_CODE ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM INVT_PENSION_ACC_ADD WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND ACCOUNT_NUM = #{accountNum}
ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
