mapper.insert:
INSERT INTO IRP_ACC_HIST
(
    MEMBER_ID
    ,ORG_CD
    ,ACCOUNT_NUM
    ,TRANS_DTIME
    ,TRANS_TYPE
    ,TRANS_NO
    ,TRANS_AMT
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{accountNum, jdbcType=VARCHAR}
    ,#{transDtime, jdbcType=VARCHAR}
    ,#{transType, jdbcType=VARCHAR}
    ,#{transNo, jdbcType=VARCHAR}
    ,#{transAmt, jdbcType=NUMERIC}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE IRP_ACC_HIST
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,ACCOUNT_NUM = #{accountNum, jdbcType=VARCHAR}
       ,TRANS_DTIME = #{transDtime, jdbcType=VARCHAR}
       ,TRANS_TYPE = #{transType, jdbcType=VARCHAR}
       ,TRANS_NO = #{transNo, jdbcType=VARCHAR}
       ,TRANS_AMT = #{transAmt, jdbcType=NUMERIC}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}
   AND TRANS_DTIME = #{transDtime}

mapper.delete:
DELETE FROM IRP_ACC_HIST
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}
   AND TRANS_DTIME = #{transDtime}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM IRP_ACC_HIST
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND ACCOUNT_NUM = #{accountNum}


    AND TRANS_DTIME = #{transDtime}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM, TRANS_DTIME) ROW_NUM ,MEMBER_ID ,ORG_CD ,ACCOUNT_NUM ,TRANS_DTIME ,TRANS_TYPE ,TRANS_NO ,TRANS_AMT ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM IRP_ACC_HIST WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND ACCOUNT_NUM = #{accountNum}
AND TRANS_DTIME = #{transDtime}
ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM, TRANS_DTIME ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
