mapper.insert:
INSERT INTO ITFN_ACC
(
    MEMBER_ID
    ,ORG_CD
    ,ACCOUNT_NUM
    ,IS_CONSENT
    ,SEQNO
    ,PROD_NAME
    ,ACCOUNT_TYPE
    ,ACCOUNT_STATUS
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{accountNum, jdbcType=VARCHAR}
    ,#{isConsent, jdbcType=VARCHAR}
    ,#{seqno, jdbcType=VARCHAR}
    ,#{prodName, jdbcType=VARCHAR}
    ,#{accountType, jdbcType=VARCHAR}
    ,#{accountStatus, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE ITFN_ACC
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,ACCOUNT_NUM = #{accountNum, jdbcType=VARCHAR}
       ,IS_CONSENT = #{isConsent, jdbcType=VARCHAR}
       ,SEQNO = #{seqno, jdbcType=VARCHAR}
       ,PROD_NAME = #{prodName, jdbcType=VARCHAR}
       ,ACCOUNT_TYPE = #{accountType, jdbcType=VARCHAR}
       ,ACCOUNT_STATUS = #{accountStatus, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}

mapper.delete:
DELETE FROM ITFN_ACC
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM ITFN_ACC
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND ACCOUNT_NUM = #{accountNum}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM) ROW_NUM ,MEMBER_ID ,ORG_CD ,ACCOUNT_NUM ,IS_CONSENT ,SEQNO ,PROD_NAME ,ACCOUNT_TYPE ,ACCOUNT_STATUS ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM ITFN_ACC WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND ACCOUNT_NUM = #{accountNum}
ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
