mapper.update:
UPDATE ITFN_LOAN
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,ACCOUNT_NUM = #{accountNum, jdbcType=VARCHAR}
       ,SEQNO = #{seqno, jdbcType=VARCHAR}
       ,ISSUE_DATE = #{issueDate, jdbcType=VARCHAR}
       ,EXP_DATE = #{expDate, jdbcType=VARCHAR}
       ,LAST_OFFERED_RATE = #{lastOfferedRate, jdbcType=NUMERIC}
       ,REPAY_DATE = #{repayDate, jdbcType=VARCHAR}
       ,REPAY_METHOD = #{repayMethod, jdbcType=VARCHAR}
       ,REPAY_ORG_CODE = #{repayOrgCode, jdbcType=VARCHAR}
       ,REPAY_ACCOUNT_NUM = #{repayAccountNum, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}
   AND SEQNO = #{seqno}

mapper.delete:
DELETE FROM ITFN_LOAN
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND ACCOUNT_NUM = #{accountNum}
   AND SEQNO = #{seqno}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM ITFN_LOAN
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND ACCOUNT_NUM = #{accountNum}


    AND SEQNO = #{seqno}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM, SEQNO) ROW_NUM ,MEMBER_ID ,ORG_CD ,ACCOUNT_NUM ,SEQNO ,ISSUE_DATE ,EXP_DATE ,LAST_OFFERED_RATE ,REPAY_DATE ,REPAY_METHOD ,REPAY_ORG_CODE ,REPAY_ACCOUNT_NUM ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM ITFN_LOAN WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND ACCOUNT_NUM = #{accountNum}
AND SEQNO = #{seqno}
ORDER BY MEMBER_ID, ORG_CD, ACCOUNT_NUM, SEQNO ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
