mapper.update:
UPDATE MEMBER
   SET
      CI                = #{ci, jdbcType=VARCHAR}
     ,MEMBER_RN         = #{memberRn, jdbcType=VARCHAR}
     ,MEMBER_PWD        = #{memberPwd, jdbcType=VARCHAR}
     ,MEMBER_NAME       = #{memberName, jdbcType=VARCHAR}
     ,MEMBER_STATUS     = #{memberStatus, jdbcType=VARCHAR}
     ,JOIN_DT           = #{joinDt, jdbcType=VARCHAR}
     ,LEAVE_DT          = #{leaveDt, jdbcType=VARCHAR}
     ,LAST_CONTACT_DT   = #{lastContactDt, jdbcType=VARCHAR}
     ,CHG_USER_ID       = #{chgUserId}
     ,CHG_DT            = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT COUNT(MEMBER_ID) FROM MEMBER WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND MEMBER_Name LIKE CONCAT('%', #{memberName}, '%')

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY M.MEMBER_NAME, M.MEMBER_ID) ROW_NUM ,M.MEMBER_ID ,M.CI ,M.MEMBER_RN ,M.MEMBER_PWD ,M.MEMBER_NAME ,M.MEMBER_STATUS ,M.JOIN_DT ,M.LEAVE_DT ,M.LAST_CONTACT_DT ,M.REG_USER_ID ,M.REG_DT ,M.CHG_USER_ID ,M.CHG_DT FROM MEMBER M WHERE 1 = 1
AND M.MEMBER_ID = #{memberId}
AND M.MEMBER_NAME LIKE CONCAT('%', #{memberName}, '%')
ORDER BY M.MEMBER_NAME, M.MEMBER_ID ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
