mapper.insert:
INSERT INTO ORG_MST
(
    ORG_CD
    ,ORG_TYPE
    ,IDST_TYPE
    ,ORG_NM
    ,ORG_SHORT_NM
    ,ORG_STATUS
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES
(
    #{orgCd, jdbcType=VARCHAR}
    ,#{orgType, jdbcType=VARCHAR}
    ,#{idstType, jdbcType=VARCHAR}
    ,#{orgNm, jdbcType=VARCHAR}
    ,#{orgShortNm, jdbcType=VARCHAR}
    ,#{orgStatus, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE ORG_MST
SET
    ORG_CD = #{orgCd, jdbcType=VARCHAR}
  ,ORG_TYPE = #{orgType, jdbcType=VARCHAR}
  ,IDST_TYPE = #{idstType, jdbcType=VARCHAR}
  ,ORG_NM = #{orgNm, jdbcType=VARCHAR}
  ,ORG_SHORT_NM = #{orgShortNm, jdbcType=VARCHAR}
  ,ORG_STATUS = #{orgStatus, jdbcType=VARCHAR}
  ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
  ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
WHERE
    ORG_CD = #{orgCd}
  AND ORG_TYPE = #{orgType}
  AND IDST_TYPE = #{idstType}

mapper.delete:
DELETE FROM ORG_MST
WHERE
    ORG_CD = #{orgCd}
  AND ORG_TYPE = #{orgType}
  AND IDST_TYPE = #{idstType}

mapper.select:
SELECT
COUNT(*)
FROM ORG_MST
WHERE 1 = 1

    AND ORG_CD = #{orgCd}


    AND ORG_TYPE = #{orgType}


    AND IDST_TYPE = #{idstType}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY ORG_CD, ORG_TYPE, IDST_TYPE) ROW_NUM ,ORG_CD ,ORG_TYPE ,IDST_TYPE ,ORG_NM ,ORG_SHORT_NM ,ORG_STATUS ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM ORG_MST WHERE 1 = 1
AND ORG_CD = #{orgCd}
AND ORG_TYPE = #{orgType}
AND IDST_TYPE = #{idstType}
ORDER BY ORG_CD, ORG_TYPE, IDST_TYPE ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
