mapper.update:
UPDATE P2P_LOAN_ADD
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,LENDING_ID = #{lendingId, jdbcType=VARCHAR}
       ,BALANCE_AMT = #{balanceAmt, jdbcType=NUMERIC}
       ,NEXT_REPAY_CNT = #{nextRepayCnt, jdbcType=NUMERIC}
       ,NEXT_REPAY_DATE = #{nextRepayDate, jdbcType=VARCHAR}
       ,PRINCIPAL_AMT = #{principalAmt, jdbcType=NUMERIC}
       ,INT_AMT = #{intAmt, jdbcType=NUMERIC}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND LENDING_ID = #{lendingId}

mapper.delete:
DELETE FROM P2P_LOAN_ADD
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND LENDING_ID = #{lendingId}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM P2P_LOAN_ADD
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND LENDING_ID = #{lendingId}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, LENDING_ID) ROW_NUM ,MEMBER_ID ,ORG_CD ,LENDING_ID ,BALANCE_AMT ,NEXT_REPAY_CNT ,NEXT_REPAY_DATE ,PRINCIPAL_AMT ,INT_AMT ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM P2P_LOAN_ADD WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND LENDING_ID = #{lendingId}
ORDER BY MEMBER_ID, ORG_CD, LENDING_ID ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
