    MEMBER_ID
    ,ORG_CD
    ,LENDING_ID
    ,EXP_DATE
    ,OFFERED_RATE
    ,REPAY_METHOD
    ,LTV_RATE
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{lendingId, jdbcType=VARCHAR}
    ,#{expDate, jdbcType=VARCHAR}
    ,#{offeredRate, jdbcType=NUMERIC}
    ,#{repayMethod, jdbcType=VARCHAR}
    ,#{ltvRate, jdbcType=NUMERIC}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE P2P_LOAN_BASE
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,LENDING_ID = #{lendingId, jdbcType=VARCHAR}
       ,EXP_DATE = #{expDate, jdbcType=VARCHAR}
       ,OFFERED_RATE = #{offeredRate, jdbcType=NUMERIC}
       ,REPAY_METHOD = #{repayMethod, jdbcType=VARCHAR}
       ,LTV_RATE = #{ltvRate, jdbcType=NUMERIC}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND LENDING_ID = #{lendingId}

mapper.delete:
DELETE FROM P2P_LOAN_BASE
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND LENDING_ID = #{lendingId}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM P2P_LOAN_BASE
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND LENDING_ID = #{lendingId}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, LENDING_ID) ROW_NUM ,MEMBER_ID ,ORG_CD ,LENDING_ID ,EXP_DATE ,OFFERED_RATE ,REPAY_METHOD ,LTV_RATE ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM P2P_LOAN_BASE WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND LENDING_ID = #{lendingId}
ORDER BY MEMBER_ID, ORG_CD, LENDING_ID ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
