mapper.update:
UPDATE P2P_LOAN
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,LENDING_ID = #{lendingId, jdbcType=VARCHAR}
       ,IS_CONSENT = #{isConsent, jdbcType=VARCHAR}
       ,TYPE = #{type, jdbcType=VARCHAR}
       ,LENDING_AMT = #{lendingAmt, jdbcType=NUMERIC}
       ,ISSUE_DATE = #{issueDate, jdbcType=VARCHAR}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND LENDING_ID = #{lendingId}

mapper.delete:
DELETE FROM P2P_LOAN
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND LENDING_ID = #{lendingId}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM P2P_LOAN
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND LENDING_ID = #{lendingId}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, LENDING_ID) ROW_NUM ,MEMBER_ID ,ORG_CD ,LENDING_ID ,IS_CONSENT ,TYPE ,LENDING_AMT ,ISSUE_DATE ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM P2P_LOAN WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND LENDING_ID = #{lendingId}
ORDER BY MEMBER_ID, ORG_CD, LENDING_ID ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
