mapper.insert:
INSERT INTO PPAY_BAL
(
    MEMBER_ID
    ,ORG_CD
    ,PP_ID
    ,TOTAL_BALANCE_AMT
    ,CHARGE_BALANCE_AMT
    ,RESERVE_BALANCE_AMT
    ,RESERVE_DUE_AMT
    ,EXP_DUE_AMT
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
    ,API_TRAN_DAY
    ,API_TRAN_ID
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{ppId, jdbcType=VARCHAR}
    ,#{totalBalanceAmt, jdbcType=VARCHAR}
    ,#{chargeBalanceAmt, jdbcType=NUMERIC}
    ,#{reserveBalanceAmt, jdbcType=NUMERIC}
    ,#{reserveDueAmt, jdbcType=NUMERIC}
    ,#{expDueAmt, jdbcType=NUMERIC}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{apiTranDay, jdbcType=VARCHAR}
    ,#{apiTranId, jdbcType=VARCHAR}
)

mapper.update:
UPDATE PPAY_BAL
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,PP_ID = #{ppId, jdbcType=VARCHAR}
       ,TOTAL_BALANCE_AMT = #{totalBalanceAmt, jdbcType=VARCHAR}
       ,CHARGE_BALANCE_AMT = #{chargeBalanceAmt, jdbcType=NUMERIC}
       ,RESERVE_BALANCE_AMT = #{reserveBalanceAmt, jdbcType=NUMERIC}
       ,RESERVE_DUE_AMT = #{reserveDueAmt, jdbcType=NUMERIC}
       ,EXP_DUE_AMT = #{expDueAmt, jdbcType=NUMERIC}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND PP_ID = #{ppId}

mapper.delete:
DELETE FROM PPAY_BAL
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND PP_ID = #{ppId}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM PPAY_BAL
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND PP_ID = #{ppId}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, PP_ID) ROW_NUM ,MEMBER_ID ,ORG_CD ,PP_ID ,TOTAL_BALANCE_AMT ,CHARGE_BALANCE_AMT ,RESERVE_BALANCE_AMT ,RESERVE_DUE_AMT ,EXP_DUE_AMT ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT ,API_TRAN_DAY ,API_TRAN_ID FROM PPAY_BAL WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND PP_ID = #{ppId}
ORDER BY MEMBER_ID, ORG_CD, PP_ID ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
