mapper.insert:
INSERT INTO PPAY
(
    MEMBER_ID
    ,ORG_CD
    ,PP_ID
    ,PP_NAME
    ,IS_CONSENT
    ,REG_DATE
    ,LIMIT_AMT
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
    ,API_TRAN_DAY
    ,API_TRAN_ID
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{ppId, jdbcType=VARCHAR}
    ,#{ppName, jdbcType=VARCHAR}
    ,#{isConsent, jdbcType=VARCHAR}
    ,#{regDate, jdbcType=VARCHAR}
    ,#{limitAmt, jdbcType=NUMERIC}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{apiTranDay, jdbcType=VARCHAR}
    ,#{apiTranId, jdbcType=VARCHAR}
)

mapper.update:
UPDATE PPAY
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,PP_ID = #{ppId, jdbcType=VARCHAR}
       ,PP_NAME = #{ppName, jdbcType=VARCHAR}
       ,IS_CONSENT = #{isConsent, jdbcType=VARCHAR}
       ,REG_DATE = #{regDate, jdbcType=VARCHAR}
       ,LIMIT_AMT = #{limitAmt, jdbcType=NUMERIC}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND PP_ID = #{ppId}

mapper.delete:
DELETE FROM PPAY
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND PP_ID = #{ppId}

DELETE FROM PPAY
WHERE
    MEMBER_ID = #{memberId}
  AND ORG_CD = #{orgCd}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM PPAY
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND PP_ID = #{ppId}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, PP_ID) ROW_NUM ,MEMBER_ID ,ORG_CD ,PP_ID ,PP_NAME ,IS_CONSENT ,REG_DATE ,LIMIT_AMT ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT ,API_TRAN_DAY ,API_TRAN_ID FROM PPAY WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND PP_ID = #{ppId}
ORDER BY MEMBER_ID, ORG_CD, PP_ID ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
