mapper.insert:
INSERT INTO RECV_BASELINE
(
    MEMBER_ID
    ,ORG_CD
    ,API_ID
    ,LAST_RECV_DATA
    ,LAST_RECV_DT
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{apiId, jdbcType=VARCHAR}
    ,#{lastRecvData, jdbcType=VARCHAR}
    ,#{lastRecvDt, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE RECV_BASELINE
SET
    MEMBER_ID = #{memberId, jdbcType=VARCHAR}
   ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
   ,API_ID = #{apiId, jdbcType=VARCHAR}
   ,LAST_RECV_DATA = #{lastRecvData, jdbcType=VARCHAR}
   ,LAST_RECV_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
   ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
   ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
WHERE
    MEMBER_ID = #{memberId}
  AND ORG_CD = #{orgCd}
  AND API_ID = #{apiId}

mapper.delete:
DELETE FROM RECV_BASELINE
WHERE
    MEMBER_ID = #{memberId}
  AND ORG_CD = #{orgCd}
  AND API_ID = #{apiId}

mapper.select:
SELECT
COUNT(*)
FROM RECV_BASELINE
WHERE 1 = 1

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND API_ID = #{apiId}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT MEMBER_ID ,ORG_CD ,API_ID ,LAST_RECV_DATA ,LAST_RECV_DT ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM RECV_BASELINE WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND API_ID = #{apiId}
) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
