mapper.insert:
INSERT INTO RECV_HIST_BASE
(
    RECV_SEQ
    ,MEMBER_ID
    ,ORG_CD
    ,API_ID
    ,RECV_START_DT
    ,RECV_END_DT
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES
(
    #{recvSeq, jdbcType=VARCHAR}
    ,#{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{apiId, jdbcType=VARCHAR}
    ,#{recvStartDt, jdbcType=VARCHAR}
    ,#{recvEndDt, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE RECV_HIST_BASE
SET
    RECV_SEQ = #{recvSeq, jdbcType=VARCHAR}
  ,MEMBER_ID = #{memberId, jdbcType=VARCHAR}
  ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
  ,API_ID = #{apiId, jdbcType=VARCHAR}
  ,RECV_START_DT = #{recvStartDt, jdbcType=VARCHAR}
  ,RECV_END_DT = #{recvEndDt, jdbcType=VARCHAR}
  ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
  ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
WHERE
    RECV_SEQ = #{recvSeq}
  AND MEMBER_ID = #{memberId}
  AND ORG_CD = #{orgCd}
  AND API_ID = #{apiId}

mapper.delete:
DELETE FROM RECV_HIST_BASE
WHERE
    RECV_SEQ = #{recvSeq}
  AND MEMBER_ID = #{memberId}
  AND ORG_CD = #{orgCd}
  AND API_ID = #{apiId}

mapper.select:
SELECT
COUNT(*)
FROM RECV_HIST_BASE
WHERE 1 = 1

    AND RECV_SEQ = #{recvSeq}


    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND API_ID = #{apiId}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY RECV_SEQ, MEMBER_ID, ORG_CD, API_ID) ROW_NUM ,RECV_SEQ ,MEMBER_ID ,ORG_CD ,API_ID ,RECV_START_DT ,RECV_END_DT ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM RECV_HIST_BASE WHERE 1 = 1
AND RECV_SEQ = #{recvSeq}
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND API_ID = #{apiId}
ORDER BY RECV_SEQ, MEMBER_ID, ORG_CD, API_ID ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
