mapper.insert:
INSERT INTO TELE_CONT
(
    MEMBER_ID
    ,ORG_CD
    ,MGMT_ID
    ,IS_CONSENT
    ,TELECOM_NUM
    ,TYPE
    ,STATUS
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{mgmtId, jdbcType=VARCHAR}
    ,#{isConsent, jdbcType=VARCHAR}
    ,#{telecomNum, jdbcType=VARCHAR}
    ,#{type, jdbcType=VARCHAR}
    ,#{status, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE TELE_CONT
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,MGMT_ID = #{mgmtId, jdbcType=VARCHAR}
       ,IS_CONSENT = #{isConsent, jdbcType=VARCHAR}
       ,TELECOM_NUM = #{telecomNum, jdbcType=VARCHAR}
       ,TYPE = #{type, jdbcType=VARCHAR}
       ,STATUS = #{status, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND MGMT_ID = #{mgmtId}

mapper.delete:
DELETE FROM TELE_CONT
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND MGMT_ID = #{mgmtId}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM TELE_CONT
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND MGMT_ID = #{mgmtId}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, MGMT_ID) ROW_NUM ,MEMBER_ID ,ORG_CD ,MGMT_ID ,IS_CONSENT ,TELECOM_NUM ,TYPE ,STATUS ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM TELE_CONT WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND MGMT_ID = #{mgmtId}
ORDER BY MEMBER_ID, ORG_CD, MGMT_ID ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
