mapper.insert:
INSERT INTO TELE_HIST
(
    MEMBER_ID
    ,ORG_CD
    ,MGMT_ID
    ,TRANS_MONTH
    ,PAID_AMT
    ,PAY_METHOD
    ,API_TRAN_DAY
    ,API_TRAN_ID
    ,REG_USER_ID
    ,REG_DT
    ,CHG_USER_ID
    ,CHG_DT
) VALUES 
(
    #{memberId, jdbcType=VARCHAR}
    ,#{orgCd, jdbcType=VARCHAR}
    ,#{mgmtId, jdbcType=VARCHAR}
    ,#{transMonth, jdbcType=VARCHAR}
    ,#{paidAmt, jdbcType=NUMERIC}
    ,#{payMethod, jdbcType=VARCHAR}
    ,#{apiTranDay, jdbcType=VARCHAR}
    ,#{apiTranId, jdbcType=VARCHAR}
    ,#{regUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
    ,#{chgUserId, jdbcType=VARCHAR}
    ,TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
)

mapper.update:
UPDATE TELE_HIST
   SET 
       MEMBER_ID = #{memberId, jdbcType=VARCHAR}
       ,ORG_CD = #{orgCd, jdbcType=VARCHAR}
       ,MGMT_ID = #{mgmtId, jdbcType=VARCHAR}
       ,TRANS_MONTH = #{transMonth, jdbcType=VARCHAR}
       ,PAID_AMT = #{paidAmt, jdbcType=NUMERIC}
       ,PAY_METHOD = #{payMethod, jdbcType=VARCHAR}
       ,API_TRAN_DAY = #{apiTranDay, jdbcType=VARCHAR}
       ,API_TRAN_ID = #{apiTranId, jdbcType=VARCHAR}
       ,CHG_USER_ID = #{chgUserId, jdbcType=VARCHAR}
       ,CHG_DT = TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS')
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND MGMT_ID = #{mgmtId}

mapper.delete:
DELETE FROM TELE_HIST
 WHERE 
       MEMBER_ID = #{memberId}
   AND ORG_CD = #{orgCd}
   AND MGMT_ID = #{mgmtId}

mapper.select:
  SELECT 
      COUNT(*) 
 FROM TELE_HIST
WHERE 1 = 1 

    AND MEMBER_ID = #{memberId}


    AND ORG_CD = #{orgCd}


    AND MGMT_ID = #{mgmtId}

SELECT T.ROW_NUM AS ROW_NUM ,T.* FROM ( SELECT ROW_NUMBER() OVER(ORDER BY MEMBER_ID, ORG_CD, MGMT_ID) ROW_NUM ,MEMBER_ID ,ORG_CD ,MGMT_ID ,TRANS_MONTH ,PAID_AMT ,PAY_METHOD ,API_TRAN_DAY ,API_TRAN_ID ,REG_USER_ID ,REG_DT ,CHG_USER_ID ,CHG_DT FROM TELE_HIST WHERE 1 = 1
AND MEMBER_ID = #{memberId}
AND ORG_CD = #{orgCd}
AND MGMT_ID = #{mgmtId}
ORDER BY MEMBER_ID, ORG_CD, MGMT_ID ) T
WHERE T.ROW_NUM BETWEEN #{startIndex} AND #{endIndex}
